SELECT BOOK.AUTHOR_ID,AUTHOR.AUTHOR_NAME,BOOK.CATEGORY,SUM(SALES*PRICE) AS TOTAL_SALES FROM BOOK, AUTHOR,BOOK_SALES WHERE BOOK.AUTHOR_ID = AUTHOR.AUTHOR_ID AND BOOK_SALES.BOOK_ID = BOOK.BOOK_ID AND SALES_DATE LIKE '2022-01-%' GROUP BY BOOK.AUTHOR_ID,CATEGORY ORDER BY BOOK.AUTHOR_ID,CATEGORY DESC;